import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [targetDt, setTargetDt] = useState("20240601");

  const fetchMovies = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(
        "http://localhost:8080/api/dailyBoxOffice",
        {
          params: { targetDt, itemPerPage: "10" },
        }
      );
      console.log("API response:", response.data);
      const movieData = response.data.boxOfficeResult?.dailyBoxOfficeList || [];
      setMovies(movieData);
    } catch (error) {
      setError(error.message);
      console.error("Fetch error:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchMovies();
  }, []);

  const handleDateChange = (e) => {
    setTargetDt(e.target.value.replace(/-/g, ""));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetchMovies();
  };

  return (
    <div className="container">
      <h1>일별 박스오피스</h1>
      <form onSubmit={handleSubmit}>
        <label>
          조회 날짜:
          <input
            type="date"
            value={targetDt.replace(/(\d{4})(\d{2})(\d{2})/, "$1-$2-$3")}
            onChange={handleDateChange}
          />
        </label>
        <button type="submit">조회</button>
      </form>
      {loading && <div>Loading...</div>}
      {error && <div>Error: {error}</div>}
      {!loading && !error && movies.length === 0 && (
        <div>데이터가 없습니다.</div>
      )}
      {movies.length > 0 && (
        <table>
          <thead>
            <tr>
              <th>순위</th>
              <th>영화명</th>
              <th>개봉일</th>
              <th>매출액</th>
              <th>관객수</th>
              <th>스크린수</th>
            </tr>
          </thead>
          <tbody>
            {movies.map((movie) => (
              <tr key={movie.movieCd}>
                <td>{movie.rank}</td>
                <td>{movie.movieNm}</td>
                <td>{movie.openDt}</td>
                <td>{movie.salesAmt}</td>
                <td>{movie.audiCnt}</td>
                <td>{movie.scrnCnt}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default App;
