import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css"; // 스타일링용 CSS 파일

function App() {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // 백엔드 API 호출
    axios
      .get("http://localhost:8080/api/dailyBoxOffice", {
        params: {
          targetDt: "20250601", // 테스트용 날짜
          itemPerPage: "10",
          // multiMovieYn, repNationCd, wideAreaCd는 필요 시 추가
        },
      })
      .then((response) => {
        const movieData = response.data.boxOfficeResult.dailyBoxOfficeList;
        setMovies(movieData);
        setLoading(false);
      })
      .catch((error) => {
        setError(error.message);
        setLoading(false);
      });
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="container">
      <h1>일별 박스오피스</h1>
      <table>
        <thead>
          <tr>
            <th>순위</th>
            <th>영화명</th>
            <th>개봉일</th>
            <th>매출액</th>
            <th>관객수</th>
            <th>스크린수</th>
          </tr>
        </thead>
        <tbody>
          {movies.map((movie) => (
            <tr key={movie.movieCd}>
              <td>{movie.rank}</td>
              <td>{movie.movieNm}</td>
              <td>{movie.openDt}</td>
              <td>{movie.salesAmt}</td>
              <td>{movie.audiCnt}</td>
              <td>{movie.scrnCnt}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
